import { useState } from "react";
import GInput from "../GInput/GInput";
import MyButton from "../MyButton/MyButton";
import classes from "./AuthorizationMenu.module.css";


const AuthorizationMenu = () => {
    
    const buttonStyle ={
        color: "black" , 
        border: "2px solid black",
        backgroundColor: "rgb(54,209,96)",
    }

    const [registrationVisible , setRegistrationVisible] = useState(false)

    const registrationClasses = [classes.visible]

    return (
        <div>

            <div className={classes.content}> 
                <div className={classes.greetings}>
                    Добрейшего времени суток!
                </div>

                <div className={classes.input}>

                    <GInput
                        type = 'text' 
                        placeholder = 'Введите почту' 
                    />

                    <GInput
                    type = 'text' 
                    placeholder = 'Введите пароль' 
                    />


                    <div className={registrationClasses}>
                        <GInput
                        type = 'text' 
                        placeholder = 'Повторите пароль' 
                        />
                    </div>

                </div>
            </div>


                <div className={classes.button}>

                    <MyButton style = {buttonStyle}>
                        Авторизироваться
                    </MyButton>
                    
                    <MyButton style = {buttonStyle}
                    onClick = { () => { 
                        if (registrationVisible === false)
                        {
                            setRegistrationVisible(JSON.stringify(true));
                            registrationClasses.push(classes.active)
                        }
                        console.log(registrationClasses)

                    }}>
                        Зарегистрироваться
                    </MyButton>

                </div>
        </div>

        
    );

};


export default AuthorizationMenu;
