import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import cl from './Carousel.module.css'

const Carousel = () => {
    // const settings = {
    //     dots: true,
    //     infinite: true,
    //     speed: 500,
    //     slidesToShow: 2,
    //     slidesToScroll: 1,
    //     autoplay: true,
    //     autoplaySpeed: 2500,
    //     arrows: false,
    // };
    const settings = {
        centerMode: true,
        infinite: true,
        centerPadding: "60px",
        slidesToShow: 3,
        speed: 500,
        responsive: [
            {
                breakpoint: 768,
                settings: {
                    arrows: false,
                    centerMode: true,
                    centerPadding: "40px",
                    slidesToShow: 3
                }
            },
            {
                breakpoint: 480,
                settings: {
                    arrows: false,
                    centerMode: true,
                    centerPadding: "40px",
                    slidesToShow: 1
            }
            }
    ]}

    return (
        <div className={cl.carousel}>
            <div className={cl.sliderTitle}>Собственное производство</div>
            <div className={cl.unusualPhoto}>
                <img src={require('../../photos/production/BKL_0873.jpg')} alt='Upload error' className={cl.unusualPhoto} />
                <img src={require('../../photos/production/BKL_0861.jpg')} alt='Upload error' className={cl.unusualPhoto} />
            </div>
            <Slider {...settings}>
                <img src={require('../../photos/production/BKL_9167.jpg')} alt='Upload error' />
                <img src={require('../../photos/production/BKL_9126.jpg')} alt='Upload error' />
                <img src={require('../../photos/restorationDiatone2017/BKL_4930.jpg')} alt='Upload error' />
                <img src={require('../../photos/TLM12015/TLM1/1600/BKL_9612.jpg')} alt='Upload error' />
            </Slider>
        </div>
    );
};

export default Carousel;
